contracts/DAORegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./ContributorRegistry.sol";
import "./RoundManager.sol";
import "./PaymentStream.sol";

/* This is the Rhada parent contract. Here is the following flow:
 * DAOs will register to join the Rhada protocol and will list the following:
 *      - Address of the contract/user that will interact with the Rhada Protocol
 *      - Funds/Fund type: funds can come later but addresses of the DAO's ERC20 token
 * Upon registering,
 *      - Will deploy a new ContributorRegistry contract specific to that DAO
 *              - Manages devs, voting process for who is a contributor
 *              - Need to launch here, don't need in struct. RoundManager uses its data, not this contract
 *      - Will deploy a new RoundVoting contract specific to that DAO
 *              - Manages rounds and voting on specific users in a round. Used to define how salary gets split
 *              - Possible (and probably necessary) feature: rewards DAO members for participating
 *      - Will deploy a PaymentStream contract specific to that DAO
 *              - Controls the setting up of payments
 * DAO parent address will map to address of each one
 * Contracts compiles data from all of them
 *      - ContributorRegistry
 *          - Don't know what to use from this
 *      - RoundVoting
 *          - Needs votes + users (will use to split salary among them all)
 *      - PaymentStream
 *          - Will start payment stream from this contract
 * Also managed here:
 *      - Weighted voting (DAO -> weighted voting. Can act as a user registry per DAO)
 *      - Lists all contributors, payments streams, and other important data
*/


contract DAORegistry is Ownable {
    /* ============ Datatypes ============ */
    struct DAO {
        string name;
        ContributorRegistry contributorRegistry; // Probably don't need b/c used in roundManager, RM has sufficient checks. Still need to deploy though
        RoundManager roundManager;
        PaymentStream paymentStream;
        uint256 salaryPerRound;
        uint256 salaryPeriod;
    }

    /* ============ State Variables ============ */
    // List of DAOs
    DAO[] public daoList;
    // Mapping of DAO to DAO object
    mapping(address => uint256) public addressToDAO;
    // Mapping of DAO object to parent address
    mapping(uint256 => address) public daoToAddress; // Need? Might remove later if we can't find a good front/backend use case for
    // Mapping of address to bool. True if already have salary. False otherwise. -> adjust payment if already have
    mapping(address => bool) public hasSalary;
    // Mapping of round number to address to bool. True if already paid for round. False otherwise.
    mapping(uint256 => mapping(address => bool)) public userPaidForRound;
    // Payment stream length

    event DaoRegistered(string name, uint256 daoId, address parent);

    IConstantFlowAgreementV1 private _cfa;
    ISuperfluid private _host;

    constructor (IConstantFlowAgreementV1 cfa, ISuperfluid superfluid) {
        _cfa = cfa;
        _host = superfluid;
    }


    function register(string memory name, uint8 _requiredConfirmations, uint256 _timePerRound, bool _timed, uint256 _salaryPerRound, uint256 _salaryPeriod) public {
        ContributorRegistry newRegistry = new ContributorRegistry(_requiredConfirmations);
        newRegistry.transferOwnership(msg.sender);

        PaymentStream newPaymentStream = new PaymentStream(_cfa, _host, msg.sender);
        daoList.push(DAO({
            name: name,
            contributorRegistry: newRegistry,
            roundManager: (new RoundManager(address(newRegistry), address(newPaymentStream), _timePerRound, _timed, msg.sender)),
            paymentStream: newPaymentStream,
            salaryPerRound: _salaryPerRound,
            salaryPeriod: _salaryPeriod

        }));
        emit DaoRegistered(name, daoList.length - 1, msg.sender);
    }


    function distributeSalaries(uint256 daoID) public {
        uint256 roundID = daoList[daoID].roundManager.getRoundNumber();
        // Logic should probably be moved elsewhere
        uint256 totalVotes = daoList[daoID].roundManager.getRoundVotes(roundID);
        address[] memory tmpUsers = daoList[daoID].roundManager.getRoundUsers(roundID);
        uint256[] memory votesPerUser;
        for(uint256 i = 0; i < tmpUsers.length; i++) {
            address tmpUser = tmpUsers[i];
            uint256 tmpUserVotes = daoList[daoID].roundManager.numVotes(roundID, tmpUser);
            uint256 salaryPercentage = tmpUserVotes / totalVotes * 100;
            votesPerUser[i] = salaryPercentage;
        }
        daoList[daoID].paymentStream.setUserScores(tmpUsers, votesPerUser);
        daoList[daoID].paymentStream.updatePayments();
    }
}

contracts/RoundManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;
import "./interfaces/IContributorRegistry.sol";
import "./interfaces/IPaymentStream.sol";

/**
    Simple implementation of a voting contract done on-chain.
    This contract allows confirmed members to place votes for a particular member.
    This contract exports the votes but does not define a strategy.
    Votes for registered users are placed in Rounds, and the round manager contract coordinates rounds
    for a particular DAO.
    Rounds are not currently time bound, they are flexible and minimal.

    TODO:
        * Implement timer between rounds. Probably better on the frontend, but users may also want to block abuse
        * Implement a timeout between rounds so that the parent contract has enough time to process everything (prevents abuse)
 */
contract RoundManager {

    /* ============ Datatypes ============ */
    enum Status {
        Open,
        Completed,
        Cancelled
    }

    struct Round {
        Status status;
        uint256 startTime;
        uint256 totalVotes;
    }

    /* ============ State Variables ============ */
    // Stores rounds
    Round[] public rounds;
    // The registry of a specific DAO
    IContributorRegistry public registry;
    IPaymentStream public paymentStreams;

    // List of addresses with admin privileges for a given DAO
    address[] public admins;
    // List of the addresses in the current round
    mapping(uint256 => address[]) public usersInRound;
    // Number of votes for a user in a given round
    mapping(uint256 => mapping(address => uint256)) public numVotes;
    // Mapping of round to those who have voted in that round
    mapping(uint256 => mapping(address => bool)) public hasVoted; // Or just map uint256 to address and iterate through?
    // TEMPORARY MAPPING
    mapping(address => uint256) weightedVoting; // Use weighted voting identifier as way to communicate w registry about who can/can't vote
    // Timeout
    uint256 timePerRound;
    // If the DAOs choose to consider time
    bool timed;

    /* ============ Events ============ */

    event RoundOpened(uint256 roundID);
    event RoundClosed(uint256 roundID, address closedBy);
    event VoteCast(uint256 roundID, address voter);

    /* Modifiers */

    modifier canVote(uint256 roundID) {
        require(!hasVoted[roundID][msg.sender] && getWeightedVote(msg.sender)  > 0);
        _;
    }

    modifier roundState(uint256 roundID, Status _status) {
        require(rounds[roundID].status == _status, "Round is not open");
        _;
    }

    modifier isAdmin() {
        bool ret = false;
        for(uint i = 0; !ret && i < admins.length; i++) {
            if(admins[i] == msg.sender) {
                ret = true;
            }
        }
        require(ret);
        _;
    }

    /* ============ Constructor ============ */

    constructor(address registryAddress, address _paymentStreams, uint256 _timePerRound, bool _timed, address adminAddr) {
        registry = IContributorRegistry(registryAddress);
        paymentStreams = IPaymentStream(_paymentStreams);
        admins.push(adminAddr);
        timePerRound = _timePerRound;
        timed = _timed;
    }

    /* ============ Mutating Functions ============ */

    /**
     * Opens a new round.
     * Confirms that there exist valid users passed to the function before opening a new round.
     * A new round opens with a given salary to be split  amongst the contributors following the voting period.
     * 
     *
     * @param newUsers          Addresses of the proposed users to be eligible for votes
     *
     */
    function openRound(
        address[] memory newUsers
    ) public  {
        // Check to see if only round open
        require(rounds.length == 0 || rounds[rounds.length - 1].status != Status.Open, "All prior rounds must be closed");
        // Checks to see if there are valid users in the passed array
        uint256 roundID = rounds.length == 0 ?  0 :  rounds.length - 1;
        for(uint256 i = 0; i < newUsers.length; i++) {
            address tmpUser = newUsers[i];
            if(registry.isValidVoter(tmpUser)) {
                usersInRound[roundID].push(newUsers[i]);
            }
        }

        // If there are valid users, then create a new round. Else, do not open a new round
        require(usersInRound[roundID].length > 0, "There must be a valid user");
        rounds.push(Round({
            status: Status.Open,
            startTime: block.timestamp,
            totalVotes: 0
        }));
        emit RoundOpened(roundID);
    }

    /**
     * Votes for users in a given round.
     * Confirms that the function caller is eligible to vote for a round.
     * The function caller casts his vote for the inputted addresses.
     * 
     *
     * @param roundID          The ID of the round
     * @param _for             The salary of the current round
     *
     */
    function castVote(
        uint256 roundID,
        address[] memory _for
    ) public canVote(roundID) roundState(roundID, Status.Open) {
        hasVoted[roundID][msg.sender] = true;
        for(uint256 i = 0; i < _for.length; i++) {
            numVotes[roundID][msg.sender] += getWeightedVote(msg.sender); // Add safe math later
            rounds[roundID].totalVotes += getWeightedVote(msg.sender);
        }
        emit VoteCast(roundID, msg.sender);
    }

    /**
     * Closes a given round.
     * Confirms that the given round is open.
     * The function caller casts his vote for the inputted addresses.
     * 
     *
     * @param roundID           The ID of the round
     * @param _newStatus        The new status, either closed or canceled, for the given round
     *
     */
    function closeRound(
        uint256 roundID,
        Status _newStatus
    ) public  {
        // Check for round status
        require(rounds[roundID].status == Status.Open, "The voting block is not closed");
        // Inputted round can only be either canceled or declared closed
        require(_newStatus != Status.Open, "Can only close or cancel a round");
        // Changes the round status
        rounds[roundID].status = _newStatus;
        emit RoundClosed(roundID, msg.sender);
    }

    /* ============ Getter Functions ============ */

    function getRounds() public view returns(Round[] memory) {
        return rounds;
    }

    function getRoundVotes(uint256 roundID) public view returns(uint256) {
        return rounds[roundID].totalVotes;
    }

    function getRoundUsers(uint256 roundID) public view returns(address[] memory) {
        return usersInRound[roundID];
    }

    function getRoundNumber() public view returns(uint256) {
        return rounds.length - 1;
    }

    function getRoundVotesPerUser(address user, uint256 roundID) public view returns(uint256) {
        return numVotes[roundID][user];
    }

    function getWeightedVote(address user) public view returns(uint256){
        return weightedVoting[user] + 1;
    }

    function distributeSalaries() public {
        uint256 roundID = getRoundNumber();
        // Logic should probably be moved elsewhere
        uint256 totalVotes = getRoundVotes(roundID);
        address[] memory tmpUsers = getRoundUsers(roundID);
        uint256[] memory votesPerUser;
        for(uint256 i = 0; i < tmpUsers.length; i++) {
            address tmpUser = tmpUsers[i];
            uint256 tmpUserVotes = getRoundVotesPerUser(tmpUser, roundID);
            uint256 salaryPercentage = tmpUserVotes / totalVotes * 100;
            votesPerUser[i] = salaryPercentage;
        }
        paymentStreams.setUserScores(tmpUsers, votesPerUser);
        paymentStreams.updatePayments();
    }
}

contracts/PaymentStream.sol.content:
// SPDX-License-Identifier: AGPLv3
pragma solidity ^0.8.0;

import {ISuperfluid, ISuperToken, ISuperApp, ISuperAgreement, SuperAppDefinitions} from "@superfluid-finance/ethereum-contracts/contracts/interfaces/superfluid/ISuperfluid.sol";
import {IConstantFlowAgreementV1} from "@superfluid-finance/ethereum-contracts/contracts/interfaces/agreements/IConstantFlowAgreementV1.sol";
import {SuperAppBase} from "@superfluid-finance/ethereum-contracts/contracts/apps/SuperAppBase.sol";

import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "./interfaces/IPaymentStream.sol";

contract PaymentStream is IPaymentStream, SuperAppBase {
    using EnumerableSet for EnumerableSet.AddressSet;

    uint8 ratioPct = 100;
    mapping(address => uint256) userScores;
    address[] receivers;
    address _owner; 

    IConstantFlowAgreementV1 private _cfa;
    ISuperfluid private _host;

    ISuperToken _token;
    bytes32 _lastAgreementId;

    //FIXME: only dao registry should call it
    function setUserScores(address[] memory users, uint256[] memory scores) public override{
        //require(msg.sender == _owner, "!auth");
        require(users.length == scores.length);
        receivers = users;
        for (uint256 i = 0; i < users.length; i++) {
            userScores[receivers[i]] = scores[i];
        }
    }

    function updatePayments() public override {
        require(_lastAgreementId != bytes32(0));
        int96 flowRate;
        uint256 deposit;

        (, flowRate, deposit, ) = _cfa.getFlowByID(_token, _lastAgreementId);
        _updateMultiFlow(_token, flowRate, deposit, new bytes(0));
    }

    constructor(IConstantFlowAgreementV1 cfa, ISuperfluid superfluid, address defaultReceiver) {
        assert(address(cfa) != address(0));
        assert(address(superfluid) != address(0));
        _cfa = cfa;
        _host = superfluid;
        _owner = msg.sender; 

        uint256 configWord = SuperAppDefinitions.APP_LEVEL_FINAL |
            SuperAppDefinitions.BEFORE_AGREEMENT_CREATED_NOOP |
            SuperAppDefinitions.BEFORE_AGREEMENT_UPDATED_NOOP |
            SuperAppDefinitions.BEFORE_AGREEMENT_TERMINATED_NOOP;

        //Default receiver to don't lose funds before the setUserScores
        receivers.push(defaultReceiver);
        userScores[defaultReceiver] = 1;

        _host.registerApp(configWord);
    }

    function _sumProportions() internal view returns (uint256 sum) {
        for (uint256 i = 0; i < receivers.length; i++) {
            sum += userScores[receivers[i]];
        }
    }

    function _updateMultiFlow(
        ISuperToken superToken,
        int96 flowRate,
        uint256 appAllowanceGranted,
        bytes memory ctx
    ) private returns (bytes memory newCtx) {
        uint256 sum = _sumProportions();

        newCtx = ctx;

        // in case of mfa, we underutlize the app allowance for simplicity
        int96 safeFlowRate = _cfa.getMaximumFlowRateFromDeposit(
            superToken,
            appAllowanceGranted - 1
        );
        appAllowanceGranted = _cfa.getDepositRequiredForFlowRate(
            superToken,
            safeFlowRate
        );

        // scale the flow rate and app allowance numbers
        appAllowanceGranted = (appAllowanceGranted * ratioPct) / 100;
        flowRate = (flowRate * ratioPct) / 100;

        for (uint256 i = 0; i < receivers.length; i++) {
            address to = receivers[i];
            uint256 targetAllowance = (appAllowanceGranted * userScores[to]) /
                sum;
            int96 targetFlowRate = _cfa.getMaximumFlowRateFromDeposit(
                superToken,
                targetAllowance
            );
            flowRate -= targetFlowRate;

            newCtx = new bytes(0);
            if (keccak256(ctx) == keccak256(newCtx)) {
                createOrUpdateFlow(superToken, to, targetFlowRate);
            } else {
                newCtx = createOrUpdateFlowWithCtx(
                    superToken,
                    to,
                    targetFlowRate,
                    ctx
                );
            }
        }
        assert(flowRate >= 0);
    }

    function afterAgreementCreated(
        ISuperToken superToken,
        address agreementClass,
        bytes32 agreementId,
        bytes calldata agreementData,
        bytes calldata, /*cbdata*/
        bytes calldata ctx
    ) external override onlyHost returns (bytes memory newCtx) {
        assert(agreementClass == address(_cfa));

        int96 flowRate;
        uint256 deposit;

        _token = superToken;
        _lastAgreementId = agreementId;

        (, flowRate, deposit, ) = _cfa.getFlowByID(superToken, agreementId);
        newCtx = _updateMultiFlow(superToken, flowRate, deposit, ctx);
    }

    function beforeAgreementUpdated(
        ISuperToken superToken,
        address agreementClass,
        bytes32 agreementId,
        bytes calldata, /*agreementData*/
        bytes calldata /*ctx*/
    ) external view override onlyHost returns (bytes memory cbdata) {
        assert(agreementClass == address(_cfa));
        (, int256 oldFlowRate, , ) = _cfa.getFlowByID(superToken, agreementId);
        return abi.encode(oldFlowRate);
    }

    function afterAgreementUpdated(
        ISuperToken superToken,
        address agreementClass,
        bytes32 agreementId,
        bytes calldata agreementData,
        bytes calldata, /* cbdata */
        bytes calldata ctx
    ) external override onlyHost returns (bytes memory newCtx) {
        assert(agreementClass == address(_cfa));
        int96 flowRate;
        uint256 deposit;

        _token = superToken;
        _lastAgreementId = agreementId;
        (, flowRate, deposit, ) = _cfa.getFlowByID(superToken, agreementId);
        newCtx = _updateMultiFlow(superToken, flowRate, deposit, ctx);
    }

    function afterAgreementTerminated(
        ISuperToken superToken,
        address agreementClass,
        bytes32 agreementId,
        bytes calldata agreementData,
        bytes calldata, /*cbdata*/
        bytes calldata ctx
    ) external override onlyHost returns (bytes memory newCtx) {
        assert(agreementClass == address(_cfa));

        _token = superToken;
        _lastAgreementId = agreementId;
        int96 flowRate;
        uint256 deposit;

        (, flowRate, deposit, ) = _cfa.getFlowByID(superToken, agreementId);
        newCtx = _updateMultiFlow(superToken, flowRate, deposit, ctx);
    }

    modifier onlyHost() {
        assert(msg.sender == address(_host));
        _;
    }

    /**************************************************************************
     * Utility methods for Superfluid
     *************************************************************************/
    function createOrUpdateFlowWithCtx(
        ISuperToken superToken,
        address to,
        int96 targetFlowRate,
        bytes memory ctx
    ) internal returns (bytes memory newCtx) {
        (, int96 outFlowRate, , ) = _cfa.getFlow(superToken, address(this), to);

        bytes4 selector;

        if (outFlowRate > 0) {
            selector = _cfa.updateFlow.selector;
        } else {
            selector = _cfa.createFlow.selector;
        }

        if (targetFlowRate == 0) {
            if (outFlowRate > 0) {
                selector = _cfa.deleteFlow.selector;
            } else {
                return ctx;
            }
        }

        bytes memory callData = abi.encodeWithSelector(
            selector,
            superToken,
            to,
            targetFlowRate,
            new bytes(0)
        );
        (newCtx, ) = _host.callAgreementWithContext(
            _cfa,
            callData,
            new bytes(0), // user data
            ctx
        );
    }

    function createOrUpdateFlow(
        ISuperToken superToken,
        address to,
        int96 targetFlowRate
    ) internal {
        (, int96 outFlowRate, , ) = _cfa.getFlow(superToken, address(this), to);

        bytes4 selector;

        if (outFlowRate > 0) {
            selector = _cfa.updateFlow.selector;
        } else {
            selector = _cfa.createFlow.selector;
        }

        if (targetFlowRate == 0) {
            if (outFlowRate > 0) {
                selector = _cfa.deleteFlow.selector;
            } else {
                return;
            }
        }

        bytes memory callData = abi.encodeWithSelector(
            selector,
            superToken,
            to,
            targetFlowRate,
            new bytes(0)
        );
        _host.callAgreement(_cfa, callData, "0x");
    }
}

